// SchoolList.jsx
import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { Link } from 'react-router-dom';

const SchoolList = () => {
    const [schools, setSchools] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSchools = async () => {
            try {
                const response = await axios.get('/school/');
                setSchools(response.data);
            } catch (error) {
                setError(error);
                console.error('There was an error fetching the schools!', error);
            }
        };

        fetchSchools();
    }, []);

    if (error) {
        return <div className="alert alert-danger">Error: {error.message}</div>;
    }

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Schools</h2>
            <ul className="list-group">
                {schools.map(school => (
                    <li key={school.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {school.name}
                        <Link to={`/school/${school.id}/teachers`} className="btn btn-primary btn-sm">Teachers</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SchoolList;
