import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const styles = {
    container: {
        marginTop: '20px',
    },
    listItem: {
        marginBottom: '20px',
        padding: '15px',
        border: '1px solid #ccc',
        borderRadius: '8px',
        display: 'flex',
        flexDirection: 'column',
    },
    heading: {
        fontWeight: 'bold',
        fontSize: '20px',
    },
    details: {
        marginTop: '10px',
        display: 'flex',
        justifyContent: 'space-between',
        width: '100%',
    },
    detailBox: {
        flex: '1',
        marginRight: '10px',
    },
    lastDetailBox: {
        marginRight: '0',
    },
    detailItem: {
        color: 'white',
        background: '#f8f9fa',
        padding: '5px 10px',
        borderRadius: '5px',
        margin: '5px 0px 0px 5px',
    },
    excused: {
        background: '#71ce00',
        color: 'white',
    },
    notExcused: {
        background: '#f44336',
        color: 'white',
    },
};

const StudentsGrades = () => {
    const { classId, teacherId, disciplineId } = useParams();
    const [students, setStudents] = useState([]);
    const [grades, setGrades] = useState({});
    const [gradeTypes, setGradeTypes] = useState([]);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');
    const [selectedGrade, setSelectedGrade] = useState('');
    const [selectedStudent, setSelectedStudent] = useState('');

    const fetchStudents = async () => {
        try {
            const response = await axios.get(`/students/school-class/${classId}`);
            setStudents(response.data);
        } catch (error) {
            setError('Error fetching students');
            console.error('Error fetching students:', error);
        }
    };

    const fetchGrades = async () => {
        try {
            const response = await axios.get(`/students/${teacherId}/discipline/${disciplineId}/grades`);
            const gradesData = response.data.reduce((acc, grade) => {
                acc[grade.studentId] = acc[grade.studentId] || [];
                acc[grade.studentId].push(grade);
                return acc;
            }, {});
            setGrades(gradesData);
        } catch (error) {
            setError('Error fetching grades');
            console.error('Error fetching grades:', error);
        }
    };

    const fetchGradeTypes = async () => {
        try {
            const response = await axios.get('/gradeTypes/');
            setGradeTypes(response.data);
        } catch (error) {
            setError('Error fetching grade types');
            console.error('Error fetching grade types:', error);
        }
    };

    useEffect(() => {
        fetchStudents();
        fetchGrades();
        fetchGradeTypes();
    }, [classId, teacherId, disciplineId]);

    const findGradeTypeById = (id) => {
        const gradeType = gradeTypes.find((gradeType) => gradeType.id === id);
        return gradeType ? gradeType.gradeTypeEnum : 'Unknown';
    };

    const handleAddGrade = async (event) => {
        event.preventDefault();
        try {
            await axios.post(`/grades`, { gradeTypeId: selectedGrade, studentId: selectedStudent, classId, teacherId, disciplineId });
            setSuccessMessage('Grade added successfully!');
            setTimeout(() => setSuccessMessage(''), 3000);
            setSelectedGrade('');
            setSelectedStudent('');
            fetchGrades();
        } catch (error) {
            setError('Error adding grade');
            console.error('Error adding grade:', error);
        }
    };

    const handleRemoveGrade = async (gradeId) => {
        try {
            await axios.delete(`/grades/${gradeId}`);
            setSuccessMessage('Grade removed successfully!');
            setTimeout(() => setSuccessMessage(''), 3000);
            fetchGrades();
        } catch (error) {
            setError('Error removing grade');
            console.error('Error removing grade:', error);
        }
    };

    return (
        <div className="container mt-4" style={styles.container}>
            <h2 style={styles.heading}>Grades for Class ID: {classId}, Teacher ID: {teacherId}, Discipline ID: {disciplineId}</h2>
            {error && <div className="alert alert-danger">Error: {error}</div>}
            {successMessage && <div className="alert alert-success">{successMessage}</div>}

            <form onSubmit={handleAddGrade}>
                <div className="mb-3">
                    <label htmlFor="studentSelect" className="form-label">Select Student:</label>
                    <select
                        id="studentSelect"
                        className="form-select"
                        value={selectedStudent}
                        onChange={(e) => setSelectedStudent(e.target.value)}
                        required
                    >
                        <option value="">Select Student</option>
                        {students.map((student) => (
                            <option key={student.id} value={student.id}>{student.name}</option>
                        ))}
                    </select>
                </div>
                <div className="mb-3">
                    <label htmlFor="gradeSelect" className="form-label">Add Grade:</label>
                    <select
                        id="gradeSelect"
                        className="form-select"
                        value={selectedGrade}
                        onChange={(e) => setSelectedGrade(e.target.value)}
                        required
                    >
                        <option value="">Select Grade</option>
                        {gradeTypes.map((grade) => (
                            <option key={grade.id} value={grade.id}>{grade.gradeTypeEnum}</option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary me-2">Add Grade</button>
                <Link to={`/admin/school/${classId}`} className="btn btn-secondary">Back to Class List</Link>
            </form>

            {students.length > 0 ? (
                students.map((student) => (
                    <div key={student.id} className="mt-4">
                        <h3 style={styles.heading}>{student.name}</h3>
                        <ul className="list-group">
                            {grades[student.id] && grades[student.id].length > 0 ? (
                                <li key={student.id} style={styles.listItem}>
                                    <div style={styles.details}>
                                        <div style={{ ...styles.detailBox, marginRight: '20px' }}>
                                            <strong>Grades:</strong>
                                            {grades[student.id].map((grade, index) => (
                                                <span key={index} style={styles.grade}>{findGradeTypeById(grade.gradeTypeId)}</span>
                                            ))}
                                        </div>
                                    </div>
                                </li>
                            ) : (
                                <li className="list-group-item">No grades available</li>
                            )}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No students available</p>
            )}
        </div>
    );
};

export default StudentsGrades;
