import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';

const TeacherList = () => {
    const { schoolId } = useParams();
    const [teachers, setTeachers] = useState([]);
    const [editTeacherId, setEditTeacherId] = useState(null);
    const [error, setError] = useState(null);
    const [editTeacherName, setEditTeacherName] = useState('');
    const [editTeacherEmail, setEditTeacherEmail] = useState('');

    const fetchTeachers = async () => {
        try {
            const response = await axios.get(`/teacher/school-id/${schoolId}`);
            setTeachers(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the teachers!', error);
        }
    };

    useEffect(() => {
        fetchTeachers();
    }, [schoolId]);

    const handleEditTeacher = (teacher) => {
        setEditTeacherId(teacher.id);
        setEditTeacherName(teacher.name);
        setEditTeacherEmail(teacher.email);
    };

    const handleSaveTeacher = async () => {
        try {
            await axios.put(`/teacher/${editTeacherId}`, {
                name: editTeacherName,
                email: editTeacherEmail,
                schoolId: Number(schoolId)
            });
            setEditTeacherId(null);
            fetchTeachers();
        } catch (error) {
            setError(error);
            console.error('Error saving teacher:', error);
        }
    };

    const handleDeleteTeacher = async (teacherId) => {
        try {
            await axios.delete(`/teacher/${teacherId}`);
            fetchTeachers();
        } catch (error) {
            setError(error);
            console.error('Error deleting teacher:', error);
        }
    };

    const handleAddTeacher = async () => {
        try {
            await axios.post('/teacher/', {
                name: editTeacherName,
                email: editTeacherEmail,
                schoolId: Number(schoolId)
            });
            setEditTeacherName('');
            setEditTeacherEmail('');
            fetchTeachers();
        } catch (error) {
            setError(error);
            console.error('Error adding teacher:', error);
        }
    };

    if (error) {
        return <div className="alert alert-danger">Error: {error.message}</div>;
    }

    return (
        <div className="container mt-4">
            <h2>Teachers for School ID: {schoolId}</h2>
            <ul className="list-group mb-4">
                {teachers.map(teacher => (
                    <li key={teacher.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {editTeacherId === teacher.id ? (
                            <div className="d-flex flex-column">
                                <input
                                    type="text"
                                    className="form-control mb-2"
                                    value={editTeacherName}
                                    onChange={(e) => setEditTeacherName(e.target.value)}
                                />
                                <input
                                    type="email"
                                    className="form-control mb-2"
                                    value={editTeacherEmail}
                                    onChange={(e) => setEditTeacherEmail(e.target.value)}
                                />
                            </div>
                        ) : (
                            <>
                                <span>{teacher.name}</span>
                                <span>{teacher.email}</span>
                            </>
                        )}
                        <div>
                            {editTeacherId === teacher.id ? (
                                <button className="btn btn-sm btn-success mr-2" onClick={handleSaveTeacher}>Save</button>
                            ) : (
                                <button className="btn btn-sm btn-warning mr-2" onClick={() => handleEditTeacher(teacher)}>Edit</button>
                            )}
                            <button className="btn btn-sm btn-danger" onClick={() => handleDeleteTeacher(teacher.id)}>Delete</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="card p-4">
                <h3>Add New Teacher</h3>
                <div className="mb-3">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Teacher's Name"
                        value={editTeacherName}
                        onChange={(e) => setEditTeacherName(e.target.value)}
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="email"
                        className="form-control"
                        placeholder="Teacher's Email"
                        value={editTeacherEmail}
                        onChange={(e) => setEditTeacherEmail(e.target.value)}
                    />
                </div>
                <button className="btn btn-success" onClick={handleAddTeacher}>Add Teacher</button>
            </div>
        </div>
    );
};

export default TeacherList;
