import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const useCurriculum = (classId) => {
    const [curriculum, setCurriculum] = useState(null);
    const [error, setError] = useState(null);

    const fetchCurriculum = useCallback(async () => {
        try {
            const response = await axios.get(`student-curriculum/school-class/${classId}`);
            setCurriculum(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the students!', error);
        }
    }, [classId]);

    useEffect(() => {
        fetchCurriculum();
    }, [fetchCurriculum]);

    return { curriculum, error, fetchCurriculum };
};

const CurriculumDetails = () => {
    const { schoolId, classId } = useParams();
    const { curriculum, error, fetchCurriculum} = useCurriculum(classId);
    const [editCurriculum, setEditCurriculum] = useState(null);
    const [newCurriculum, setNewCurriculum] = useState({ semester: '', year: ''});

    const handleEditCurriculum = (student) => setEditCurriculum({...student});

    const handleChange = (e, setter) => {
        const {name, value} = e.target;
        setter((prev) => ({...prev, [name]:value}));
    }

    const handleSaveCurriculum = async() => {
        try{
            await axios.put(`/students/${editCurriculum.id}`,{
                ...editCurriculum,
                schoolClassId : Number(classId)
            });
            setEditCurriculum(null);
            fetchCurriculum();
        } catch(error){
            console.error('Error saving student:', error);
        }
    };

    const handleDeleteCurriculum = async(studentId) => {
        if(window.confirm('Are you sure you want to delete this student?')) {
            try{
                await axios.delete(`/students/${studentId}`);
                fetchCurriculum();
            } catch(error) {
                console.error('Error deleting student: ', error);
            }
        }
    };

    const handleAddCurriculum = async() => {
        try{
            await axios.post(`/students/`, {
                ...newCurriculum,
                schoolClassId: Number(classId)
            });
            setNewCurriculum({name: '', numberInClass: ''});
            fetchCurriculum()
        } catch(error) {
            console.error("Error adding student: ", error);
        }
    };

    return (
        <div className="container mt-4">
            <div className="mb-4 d-flex justify-content-between align-items-center">
                <h2 className="mb-0">School Class Details for School ID: {schoolId}</h2>
                <Link to={`/admin/school/${schoolId}/class/${classId}/curriculum`}
                      className="btn btn-primary">
                    Curriculum
                </Link>
            </div>
            {error &&
                <div className="alert alert-danger text-center">
                    Error: {error.message}
                </div>
            }



            <div className="mb-4">
                <div className="card">
                    <h3 className="card-header">Add New Curriculum</h3>
                    <div className="card-body">
                        <div className="form-group">
                            <input
                                type="text"
                                className="form-control mb-3"
                                placeholder="Curriculum's Name"
                                name="name"
                                value={newCurriculum.name}
                                onChange={(e) => handleChange(e, setNewCurriculum)}
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="number"
                                className="form-control mb-3"
                                placeholder="Curriculum's Number in Class"
                                name="numberInClass"
                                value={newCurriculum.numberInClass}
                                onChange={(e) => handleChange(e, setNewCurriculum)}
                            />
                        </div>
                        <button className="btn btn-success me-1" onClick={handleAddCurriculum}>
                            Add Curriculum
                        </button>
                    </div>
                </div>
            </div>

            <ul className="list-group">
                {students.length > 0 ? (
                    students.map((student) => (
                        <li key={student.id} className="list-group-item mb-3">
                            <div className="d-flex align-items-center justify-content-between">
                                <div className="student-info">
                                    {editCurriculum && editCurriculum.id === student.id ? (
                                        <>
                                            <div className="mb-2">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    name="name"
                                                    value={editCurriculum.name}
                                                    onChange={(e) => handleChange(e, setEditCurriculum)}
                                                />
                                            </div>
                                            <div className="mb-2">
                                                <input
                                                    type="number"
                                                    className="form-control"
                                                    name="numberInClass"
                                                    value={editCurriculum.numberInClass}
                                                    onChange={(e) => handleChange(e, setEditCurriculum)}
                                                />
                                            </div>
                                        </>
                                    ) : (
                                        <>
                                            <span className="d-block mb-1">{student.name}</span>
                                            <span>{student.numberInClass}</span>
                                        </>
                                    )}
                                </div>
                                <div className="actions">
                                    {editCurriculum && editCurriculum.id === student.id ? (
                                        <button className="btn btn-sm btn-success me-1" onClick={handleSaveCurriculum}>
                                            Save
                                        </button>
                                    ) : (
                                        <button className="btn btn-sm btn-success me-1" onClick={() => handleEditCurriculum(student)}>
                                            Edit
                                        </button>
                                    )}
                                    <Link to={`/admin/school/${schoolId}/class/${classId}/student/${student.id}/caregivers`}
                                          className="btn btn-primary btn-sm me-1">
                                        Caregivers
                                    </Link>
                                    <button className="btn btn-sm btn-danger me-1" onClick={() => handleDeleteCurriculum(student.id)}>
                                        Delete
                                    </button>
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No students available</li>
                )}
            </ul>
        </div>
    );
};

export default SchoolClassDetails;
