import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useHistory } from 'react-router-dom';

const AddDiscipline = () => {
    const [disciplineName, setDisciplineName] = useState('');
    const [qualifications, setQualifications] = useState([]);
    const [selectedQualificationId, setSelectedQualificationId] = useState('');
    const [disciplines, setDisciplines] = useState([]);
    const [error, setError] = useState(null);
    const [editDiscipline, setEditDiscipline] = useState(null);
    const [editDisciplineName, setEditDisciplineName] = useState('');
    const history = useHistory();

    useEffect(() => {
        const fetchQualifications = async () => {
            try {
                const response = await axios.get('/qualifications/');
                setQualifications(response.data);
            } catch (error) {
                console.error('Error fetching qualifications:', error);
            }
        };

        const fetchDisciplines = async () => {
            try {
                const response = await axios.get('/disciplines/');
                setDisciplines(response.data);
            } catch (error) {
                console.error('Error fetching disciplines:', error);
            }
        };

        fetchQualifications();
        fetchDisciplines();
    }, []);

    const handleDisciplineNameChange = (event) => {
        setDisciplineName(event.target.value);
    };

    const handleQualificationChange = (event) => {
        setSelectedQualificationId(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await axios.post('/disciplines/', {
                name: disciplineName,
                qualificationId: selectedQualificationId
            });
            history.push('/admin'); // Redirect to the admin page or any other page
        } catch (error) {
            console.error('Error creating discipline:', error);
            setError(error);
        }
    };

    const handleEditDiscipline = (discipline) => {
        setEditDiscipline(discipline);
        setEditDisciplineName(discipline.name);
    };

    const handleEditDisciplineChange = (event) => {
        setEditDisciplineName(event.target.value);
    };

    const handleSaveDiscipline = async () => {
        try {
            const response = await axios.put(`/disciplines/${editDiscipline.id}`, {
                ...editDiscipline,
                name: editDisciplineName
            });
            setDisciplines(disciplines.map(d => (d.id === editDiscipline.id ? response.data : d)));
            setEditDiscipline(null);
            setEditDisciplineName('');
        } catch (error) {
            console.error('Error saving discipline:', error);
        }
    };

    const handleCancelEdit = () => {
        setEditDiscipline(null);
        setEditDisciplineName('');
    };

    const handleDeleteDiscipline = async (id) => {
        try {
            await axios.delete(`/disciplines/${id}`);
            setDisciplines(disciplines.filter(discipline => discipline.id !== id));
        } catch (error) {
            console.error('Error deleting discipline:', error);
        }
    };

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Add Discipline</h2>
            {error && <div className="alert alert-danger">Error: {error.message}</div>}
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="disciplineName" className="form-label">Discipline Name</label>
                    <input
                        type="text"
                        className="form-control"
                        id="disciplineName"
                        value={disciplineName}
                        onChange={handleDisciplineNameChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="qualificationSelect" className="form-label">Qualification</label>
                    <select
                        id="qualificationSelect"
                        className="form-select"
                        value={selectedQualificationId}
                        onChange={handleQualificationChange}
                        required
                    >
                        <option value="">Select Qualification</option>
                        {qualifications.map(qualification => (
                            <option key={qualification.id} value={qualification.id}>
                                {qualification.name}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Add Discipline</button>
            </form>

            <h3 className="mt-4">Disciplines</h3>
            <ul className="list-group">
                {disciplines.length > 0 ? (
                    disciplines.map(discipline => (
                        <li key={discipline.id} className="list-group-item d-flex justify-content-between align-items-center">
                            {editDiscipline && editDiscipline.id === discipline.id ? (
                                <div>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={editDisciplineName}
                                        onChange={handleEditDisciplineChange}
                                    />
                                    <button className="btn btn-sm btn-success me-1" onClick={handleSaveDiscipline}>Save</button>
                                    <button className="btn btn-sm btn-secondary" onClick={handleCancelEdit}>Cancel</button>
                                </div>
                            ) : (
                                <>
                                    {discipline.name}
                                    <div>
                                        <button
                                            className="btn btn-sm btn-success me-1"
                                            onClick={() => handleEditDiscipline(discipline)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="btn btn-sm btn-danger"
                                            onClick={() => handleDeleteDiscipline(discipline.id)}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </>
                            )}
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No disciplines available</li>
                )}
            </ul>
        </div>
    );
};

export default AddDiscipline;
