import React, {useState, useEffect, useCallback} from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const useStudents = (schoolClassId) => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState(null);

    const fetchStudents = useCallback(async () => {
        try{
            const response = await axios.get(`students/school-class/${schoolClassId}`)
        } catch (error){
            setError(error);
            console.error('There was an error fetching the students!', error);
        }
    }, [schoolClassId]);

    useEffect(() => {
        fetchStudents();
    }, [fetchStudents]);

    return {students, error, fetchStudents};
}



const SchoolClassDetails = () => {
    const { schoolId, schoolClassId } = useParams();
    const { students, error, fetchStudents } = useStudents(schoolClassId);

    return(
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Students frm Class with ID: {schoolClassId}</h2>
                {error && <div className="alert alert-danger">Error: {error.message}</div>}
            </div>

            <div className="mb-4">
                <TeacherForm
                    teacher={newTeacher}
                    onChange={(e) => handleChange(e, setNewTeacher)}
                    onSave={handleAddTeacher}
                />
            </div>

            <ul className="list-group">
                {teachers.length > 0 ? (
                    teachers.map((teacher) => (
                        <li key={teacher.id} className="list-group-item mb-3">
                            <div className="d-flex align-items-center justify-content-between">
                                <div className="teacher-info">
                                    {editTeacher && editTeacher.id === teacher.id ? (
                                        <TeacherForm
                                            teacher={editTeacher}
                                            onChange={(e) => handleChange(e, setEditTeacher)}
                                            onSave={handleSaveTeacher}
                                            onCancel={() => setEditTeacher(null)}
                                            isEdit={true}
                                        />
                                    ) : (
                                        <>
                                            <span className="d-block mb-1">{teacher.name}</span>
                                            <span>{teacher.email}</span>
                                        </>
                                    )}
                                </div>
                                <div className="actions">
                                    <Link to={`/admin/school/${schoolId}/teacher/${teacher.id}/qualifications`} className="btn btn-primary btn-sm me-1">
                                                Qualifications
                                    </Link>
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No teachers available</li>
                )}
            </ul>
        </div>

    );
};

export default SchoolClassDetails;
