import React from 'react';

const EditTeacherDiscipline = ({ item, teachers, disciplines, onSave, onCancel }) => {
    const [updatedItem, setUpdatedItem] = React.useState({ ...item });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem(prev => ({ ...prev, [name]: value }));
    };

    const handleSave = () => {
        onSave(updatedItem);
    };

    // Filter teachers based on selected discipline's teacherQualificationIds
    const filteredTeachers = React.useMemo(() => {
        if (!updatedItem.disciplineId) return teachers;

        const selectedDiscipline = disciplines.find(discipline => discipline.id === updatedItem.disciplineId);
        if (!selectedDiscipline || !selectedDiscipline.teacherQualificationIds) return teachers;

        const { teacherQualificationIds } = selectedDiscipline;
        return teachers.filter(teacher => teacherQualificationIds.includes(teacher.qualificationId));
    }, [updatedItem.disciplineId, teachers, disciplines]);

    return (
        <div>
            <div className="mb-2">
                <label htmlFor="discipline">Discipline</label>
                <select
                    className="form-control"
                    id="discipline"
                    name="disciplineId"
                    value={updatedItem.disciplineId || ''}
                    onChange={handleChange}
                >
                    <option value="" disabled>Select a discipline</option>
                    {disciplines.map(discipline => (
                        <option key={discipline.id} value={discipline.id}>
                            {discipline.name}
                        </option>
                    ))}
                </select>
            </div>
            <div className="mb-2">
                <label htmlFor="teacher">Teacher</label>
                <select
                    className="form-control"
                    id="teacher"
                    name="teacherId"
                    value={updatedItem.teacherId || ''}
                    onChange={handleChange}
                >
                    <option value="" disabled>Select a teacher</option>
                    {filteredTeachers.map(teacher => (
                        <option key={teacher.id} value={teacher.id}>
                            {teacher.name}
                        </option>
                    ))}
                </select>
            </div>
            <div className="actions">
                <button className="btn btn-sm btn-success me-1" onClick={handleSave}>Save</button>
                <button className="btn btn-sm btn-secondary" onClick={onCancel}>Cancel</button>
            </div>
        </div>
    );
};

export default EditTeacherDiscipline;
