import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const useStudents = (classId) => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState(null);

    const fetchStudents = useCallback(async () => {
        try {
            const response = await axios.get(`students/school-class/${classId}`);
            setStudents(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the students!', error);
        }
    }, [classId]);

    useEffect(() => {
        fetchStudents();
    }, [fetchStudents]);

    const updateStudent = async (studentId, updatedData) => {
        try {
            const response = await axios.put(`/students/${studentId}`, updatedData);
            const updatedStudent = response.data;
            const updatedStudents = students.map((student) =>
                student.id === updatedStudent.id ? updatedStudent : student
            );
            setStudents(updatedStudents);
        } catch (error) {
            console.error('Error updating student:', error);
        }
    };

    return { students, error, fetchStudents, updateStudent };
};

const SchoolClassDetails = () => {
    const { schoolId, classId } = useParams();
    const { students, error, updateStudent } = useStudents(classId);
    const [editableStudentId, setEditableStudentId] = useState(null);

    const handleEditClick = (studentId) => {
        setEditableStudentId(studentId);
    };

    const handleCancelEdit = () => {
        setEditableStudentId(null);
    };

    const handleSaveEdit = async (studentId, updatedData) => {
        await updateStudent(studentId, updatedData);
        setEditableStudentId(null);
    };

    const renderStudent = (student) => {
        if (editableStudentId === student.id) {
            return (
                <li key={student.id} className="list-group-item mb-3">
                    <div className="d-flex align-items-center justify-content-between">
                        <div className="students-info">
                            <input
                                type="text"
                                value={student.name}
                                onChange={(e) => handleFieldChange(student.id, 'name', e.target.value)}
                            />
                            <input
                                type="number"
                                value={student.numberInClass}
                                onChange={(e) => handleFieldChange(student.id, 'numberInClass', parseInt(e.target.value))}
                            />
                        </div>
                        <div className="actions">
                            <button className="btn btn-success btn-sm me-1" onClick={() => handleSaveEdit(student.id, student)}>
                                Save
                            </button>
                            <button className="btn btn-secondary btn-sm" onClick={handleCancelEdit}>
                                Cancel
                            </button>
                        </div>
                    </div>
                </li>
            );
        } else {
            return (
                <li key={student.id} className="list-group-item mb-3">
                    <div className="d-flex align-items-center justify-content-between">
                        <div className="students-info">
                            <span className="d-block mb-1">{student.name}</span>
                            <span>Number in Class: {student.numberInClass}</span>
                        </div>
                        <div className="actions">
                            <button className="btn btn-primary btn-sm me-1" onClick={() => handleEditClick(student.id)}>
                                Edit
                            </button>
                            <Link
                                to={`/admin/school/${schoolId}/class/${classId}/student/${student.id}/caregivers`}
                                className="btn btn-primary btn-sm"
                            >
                                Caregivers
                            </Link>
                        </div>
                    </div>
                </li>
            );
        }
    };

    const handleFieldChange = (studentId, field, value) => {
        const updatedStudents = students.map((student) =>
            student.id === studentId ? { ...student, [field]: value } : student
        );
        setStudents(updatedStudents);
    };

    return (
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Students from Class with ID: {classId}</h2>
                {error && <div className="alert alert-danger">Error: {error.message}</div>}
            </div>

            <ul className="list-group">
                {students.length > 0 ? (
                    students.map((student) => renderStudent(student))
                ) : (
                    <li className="list-group-item">No students available</li>
                )}
            </ul>
        </div>
    );
};

export default SchoolClassDetails;
