// src/components/SchoolList.jsx
import React from 'react';
import { useQuery } from '@tanstack/react-query';
import axios from '../axiosInstance';
import { Link } from 'react-router-dom';

// Function to fetch schools from the API
const fetchSchools = async () => {
    const response = await axios.get('/school/');
    return response.data;
};

const SchoolList = () => {
    // Use the useQuery hook to fetch data and manage the loading and error states
    const { data: schools, error, isLoading } = useQuery({
        queryKey: ['schools'], // Query key for caching
        queryFn: fetchSchools   // Function to fetch data
    });

    if (isLoading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div className="alert alert-danger">Error: {error.message}</div>;
    }

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Schools</h2>
            <ul className="list-group">
                {schools.map(school => (
                    <li key={school.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {school.name}
                        <div>
                            <Link to={`/school/${school.id}`} className="btn btn-primary btn-sm mr-2">Details</Link>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SchoolList;
