// TeacherList.jsx
import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';

const TeacherList = () => {
    const { schoolId } = useParams();
    const [teachers, setTeachers] = useState([]);
    const [teacherName, setTeacherName] = useState('');
    const [teacherEmail, setTeacherEmail] = useState('');
    const [error, setError] = useState(null);

    // Define fetchTeachers function to fetch teachers from backend
    const fetchTeachers = async () => {
        try {
            const response = await axios.get(`/teacher/school-id/${schoolId}`);
            setTeachers(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the teachers!', error);
        }
    };

    useEffect(() => {
        // Call fetchTeachers when component mounts or schoolId changes
        fetchTeachers();
    }, [schoolId]);

    const handleAddTeacher = async () => {
        try {
            await axios.post('/teacher/', {
                name: teacherName,
                email: teacherEmail,
                schoolId: Number(schoolId) // Pass the school ID to the backend
            });
            // Refresh the teacher list after adding
            fetchTeachers();
            // Clear input fields
            setTeacherName('');
            setTeacherEmail('');
        } catch (error) {
            setError(error);
            console.error('Error adding teacher:', error);
        }
    };

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div>
            <h2>Teachers for School ID: {schoolId}</h2>
            <ul>
                {teachers.map(teacher => (
                    <li key={teacher.id}>{teacher.name} - {teacher.email}</li>
                ))}
            </ul>
            <h3>Add New Teacher</h3>
            <input
                type="text"
                placeholder="Teacher's Name"
                value={teacherName}
                onChange={(e) => setTeacherName(e.target.value)}
            />
            <input
                type="email"
                placeholder="Teacher's Email"
                value={teacherEmail}
                onChange={(e) => setTeacherEmail(e.target.value)}
            />
            <button onClick={handleAddTeacher}>Add Teacher</button>
        </div>
    );
};

export default TeacherList;
