import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';
import { fetchAllDisciplines, fetchAllTeacherQualifications } from '../functions/fetchFunctions';
import useCurriculum from '../functions/useCurriculum';

const CurriculumDetails = () => {
    const { schoolId, classId } = useParams();
    const { curriculum, error, fetchCurriculum, setCurriculum } = useCurriculum(classId);
    const [disciplines, setDisciplines] = useState([]);
    const [qualifications, setQualifications] = useState([]);
    const [selectedDiscipline, setSelectedDiscipline] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const fetchedDisciplines = await fetchAllDisciplines();
                const fetchedQualifications = await fetchAllTeacherQualifications();
                setDisciplines(fetchedDisciplines);
                setQualifications(fetchedQualifications);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, [schoolId]);

    const toggleDiscipline = (disciplineId) => {
        setSelectedDiscipline(selectedDiscipline === disciplineId ? null : disciplineId);
    };

    return (
        <div className="container mt-4">
            <h2>Curriculum Details</h2>

            {disciplines.length > 0 && (
                <div className="card mt-4">
                    <h3 className="card-header">Disciplines and Qualifications</h3>
                    <div className="card-body">
                        <ul className="list-group">
                            {disciplines.map((discipline) => (
                                <li key={discipline.id} className="list-group-item">
                                    <div onClick={() => toggleDiscipline(discipline.id)} className="d-flex justify-content-between align-items-center">
                                        <span>{discipline.name}</span>
                                        <button className="btn btn-secondary btn-sm">
                                            {selectedDiscipline === discipline.id ? 'Hide Qualifications' : 'Show Qualifications'}
                                        </button>
                                    </div>
                                    {selectedDiscipline === discipline.id && (
                                        <ul className="list-group mt-2">
                                            {qualifications
                                                .filter((qualification) => qualification.disciplineId === discipline.id)
                                                .map((qualification) => (
                                                    <li key={qualification.id} className="list-group-item">
                                                        {qualification.name}
                                                    </li>
                                                ))}
                                        </ul>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CurriculumDetails;
