import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';
import { fetchAllTeachersFromSchool, fetchAllDisciplines } from './fetchFunctions';
import CurriculumTeachersDisciplines from './CurriculumTeachersDisciplines';

const CurriculumDetails = () => {
    const { schoolId, classId } = useParams();
    const [curriculum, setCurriculum] = useState(null);
    const [newCurriculum, setNewCurriculum] = useState({ semester: '', year: '' });
    const [teachers, setTeachers] = useState([]);
    const [disciplines, setDisciplines] = useState([]);
    const [selectedTeachers, setSelectedTeachers] = useState([]);
    const [selectedDisciplines, setSelectedDisciplines] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const fetchedTeachers = await fetchAllTeachersFromSchool(schoolId);
                const fetchedDisciplines = await fetchAllDisciplines();
                setTeachers(fetchedTeachers);
                setDisciplines(fetchedDisciplines);
            } catch (error) {
                console.error('Error fetching teachers and disciplines:', error);
            }
        };

        fetchData();
    }, [schoolId]);

    const fetchCurriculum = async () => {
        try {
            const response = await axios.get(`student-curriculum/school-class/${classId}`);
            setCurriculum(response.data);
        } catch (error) {
            console.error('There was an error fetching the curriculum!', error);
        }
    };

    useEffect(() => {
        fetchCurriculum();
    }, [classId]);

    const handleAddCurriculum = async () => {
        try {
            if (curriculum !== null) {
                console.log("A curriculum already exists for this class.");
                return;
            }

            await axios.post(`/student-curriculum/`, {
                ...newCurriculum,
                schoolClassId: Number(classId),
                teachers: selectedTeachers.map(id => ({ id })),
                disciplines: selectedDisciplines.map(id => ({ id }))
            });

            setNewCurriculum({ semester: '', year: '' });
            setSelectedTeachers([]);
            setSelectedDisciplines([]);
            fetchCurriculum();
        } catch (error) {
            console.error("Error adding curriculum: ", error);
        }
    };

    const handleEditCurriculum = async () => {
        try {
            await axios.put(`/student-curriculum/${curriculum.id}`, {
                ...curriculum,
                schoolClassId: Number(classId)
            });
            fetchCurriculum();
        } catch (error) {
            console.error('Error saving curriculum:', error);
        }
    };

    const handleDeleteCurriculum = async () => {
        if (window.confirm('Are you sure you want to delete this curriculum?')) {
            try {
                await axios.delete(`/student-curriculum/${curriculum.id}`);
                setCurriculum(null);
                fetchCurriculum();
            } catch (error) {
                console.error('Error deleting curriculum: ', error);
            }
        }
    };

    const handleChange = (e, setter) => {
        const { name, value } = e.target;
        setter(prev => ({ ...prev, [name]: value }));
    };

    return (
        <div className="container mt-4">
            <h2>Curriculum Details</h2>

            {curriculum ? (
                <div className="card">
                    <div className="card-body">
                        <h3 className="card-title">Curriculum Information</h3>
                        <p>Semester: {curriculum.semester}</p>
                        <p>Year: {curriculum.year}</p>
                        <button className="btn btn-primary" onClick={handleEditCurriculum}>Edit</button>
                        <button className="btn btn-danger" onClick={handleDeleteCurriculum}>Delete</button>
                    </div>
                </div>
            ) : (
                <div className="card">
                    <div className="card-body">
                        <h3 className="card-title">Add Curriculum</h3>
                        <div className="form-group">
                            <label htmlFor="semester">Semester:</label>
                            <input
                                type="text"
                                id="semester"
                                name="semester"
                                className="form-control"
                                value={newCurriculum.semester}
                                onChange={(e) => handleChange(e, setNewCurriculum)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="year">Year:</label>
                            <input
                                type="text"
                                id="year"
                                name="year"
                                className="form-control"
                                value={newCurriculum.year}
                                onChange={(e) => handleChange(e, setNewCurriculum)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="teacherSelect">Select Teachers:</label>
                            <select
                                id="teacherSelect"
                                className="form-control mb-3"
                                multiple
                                value={selectedTeachers}
                                onChange={(e) => setSelectedTeachers([...e.target.selectedOptions].map(option => option.value))}
                            >
                                <option value="">Select teachers</option>
                                {teachers.map(teacher => (
                                    <option key={teacher.id} value={teacher.id}>{teacher.name}</option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="disciplineSelect">Select Disciplines:</label>
                            <select
                                id="disciplineSelect"
                                className="form-control mb-3"
                                multiple
                                value={selectedDisciplines}
                                onChange={(e) => setSelectedDisciplines([...e.target.selectedOptions].map(option => option.value))}
                            >
                                <option value="">Select disciplines</option>
                                {disciplines.map(discipline => (
                                    <option key={discipline.id} value={discipline.id}>{discipline.name}</option>
                                ))}
                            </select>
                        </div>
                        <button className="btn btn-success" onClick={handleAddCurriculum}>Add Curriculum</button>
                    </div>
                </div>
            )}

            {curriculum && (
                <CurriculumTeachersDisciplines schoolId={schoolId} curriculumId={curriculum.id} />
            )}
        </div>
    );
};

export default CurriculumDetails;
