import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';

const useTeachers = (schoolId) => {
    const [teachers, setTeachers] = useState([]);
    const [error, setError] = useState(null);

    const fetchTeachers = useCallback(async () => {
        try {
            const response = await axios.get(`/teacher/school-id/${schoolId}`);
            setTeachers(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the teachers!', error);
        }
    }, [schoolId]);

    useEffect(() => {
        fetchTeachers();
    }, [fetchTeachers]);

    return { teachers, error, fetchTeachers };
};

const TeacherList = () => {
    const { schoolId } = useParams();
    const { teachers, error, fetchTeachers } = useTeachers(schoolId);
    const [editTeacher, setEditTeacher] = useState(null);
    const [newTeacher, setNewTeacher] = useState({ name: '', email: '' });

    const handleEditTeacher = (teacher) => {
        setEditTeacher({ ...teacher });
    };

    const handleChange = (e, setter) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleSaveTeacher = async () => {
        try {
            await axios.put(`/teacher/${editTeacher.id}`, {
                ...editTeacher,
                schoolId: Number(schoolId)
            });
            setEditTeacher(null);
            fetchTeachers();
        } catch (error) {
            console.error('Error saving teacher:', error);
        }
    };

    const handleDeleteTeacher = async (teacherId) => {
        if (window.confirm('Are you sure you want to delete this teacher?')) {
            try {
                await axios.delete(`/teacher/${teacherId}`);
                fetchTeachers();
            } catch (error) {
                console.error('Error deleting teacher:', error);
            }
        }
    };

    const handleAddTeacher = async () => {
        try {
            await axios.post('/teacher/', {
                ...newTeacher,
                schoolId: Number(schoolId)
            });
            setNewTeacher({ name: '', email: '' });
            fetchTeachers();
        } catch (error) {
            console.error('Error adding teacher:', error);
        }
    };

    if (error) {
        return <div className="error-message">Error: {error.message}</div>;
    }

    return (
        <div className="container mt-4">
            <h2>Teachers for School ID: {schoolId}</h2>
            <ul className="list-group mb-4">
                {teachers.map((teacher) => (
                    <li key={teacher.id} className="list-item">
                        <div className="teacher-info">
                            {editTeacher && editTeacher.id === teacher.id ? (
                                <>
                                    <input
                                        type="text"
                                        className="form-input"
                                        name="name"
                                        value={editTeacher.name}
                                        onChange={(e) => handleChange(e, setEditTeacher)}
                                    />
                                    <input
                                        type="email"
                                        className="form-input"
                                        name="email"
                                        value={editTeacher.email}
                                        onChange={(e) => handleChange(e, setEditTeacher)}
                                    />
                                </>
                            ) : (
                                <>
                                    <span>{teacher.name}</span>
                                    <span>{teacher.email}</span>
                                </>
                            )}
                        </div>
                        <div className="actions">
                            {editTeacher && editTeacher.id === teacher.id ? (
                                <button className="btn save-btn" onClick={handleSaveTeacher}>
                                    Save
                                </button>
                            ) : (
                                <button className="btn edit-btn" onClick={() => handleEditTeacher(teacher)}>
                                    Edit
                                </button>
                            )}
                            <button className="btn delete-btn" onClick={() => handleDeleteTeacher(teacher.id)}>
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="card">
                <h3>Add New Teacher</h3>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-input"
                        placeholder="Teacher's Name"
                        name="name"
                        value={newTeacher.name}
                        onChange={(e) => handleChange(e, setNewTeacher)}
                    />
                </div>
                <div className="form-group">
                    <input
                        type="email"
                        className="form-input"
                        placeholder="Teacher's Email"
                        name="email"
                        value={newTeacher.email}
                        onChange={(e) => handleChange(e, setNewTeacher)}
                    />
                </div>
                <button className="btn add-btn" onClick={handleAddTeacher}>
                    Add Teacher
                </button>
            </div>
        </div>
    );
};

export default TeacherList;
