import React from 'react';
import { useParams } from 'react-router-dom';
import useStudentData from '../hooks/useStudentData';

const StudentView = () => {
    const { studentId } = useParams();
    const { disciplines, error } = useStudentData(studentId);

    // Define inline styles
    const styles = {
        container: {
            marginTop: '20px',
        },
        listItem: {
            marginBottom: '20px',
            padding: '15px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            display: 'flex',
            flexDirection: 'column',
        },
        heading: {
            fontWeight: 'bold',
            fontSize: '20px',
        },
        details: {
            marginTop: '10px',
            display: 'flex',
            justifyContent: 'space-between',
            width: '100%',
        },
        detailBox: {
            flex: '1',
            marginRight: '10px',
        },
        lastDetailBox: {
            marginRight: '0',
        },
        detailItem: {
            padding: '5px 10px',
            borderRadius: '5px',
            margin: '5px 0px 0px 5px',
        },
        gradeA: {
            background: '#71ce00',
            color: 'white',
        },
        gradeB: {
            background: '#2993cc',
            color: 'white',
        },
        gradeC: {
            background: '#f4d536',
            color: 'white',
        },
        gradeD: {
            background: '#f47036',
            color: 'white',
        },
        gradeF: {
            background: '#f44336',
            color: 'white',
        },
        gradeUnknown: {
            background: '#f8f9fa',
        },
        excused: {
            background: '#71ce00',
            color: 'white',
        },
        notExcused: {
            background: '#f44336',
            color: 'white',
        }
    };

    const getGradeStyle = (grade) => {
        let style;
        switch (grade) {
            case 'A':
                style = styles.gradeA;
                break;
            case 'B':
                style = styles.gradeB;
                break;
            case 'C':
                style = styles.gradeC;
                break;
            case 'D':
                style = styles.gradeD;
                break;
            case 'F':
                style = styles.gradeF;
                break;
            default:
                style = styles.gradeUnknown;
        }
        return { ...styles.detailItem, ...style };
    };

    return (
        <div className="container" style={styles.container}>
            <h2>Disciplines and Results for Student ID: {studentId}</h2>
            {error && <div className="alert alert-danger">Error: {error.message}</div>}
            <ul className="list-group">
                {disciplines.length > 0 ? (
                    disciplines.map((discipline) => (
                        <li key={discipline.id} style={styles.listItem}>
                            <h4 style={styles.heading}>{discipline.name}</h4>
                            <div style={styles.details}>
                                <div style={{ ...styles.detailBox, marginRight: '20px' }}>
                                    <strong>Grades:</strong>
                                    {discipline.grades.map((grade, index) => (
                                        <span key={index} style={getGradeStyle(grade)}>
                                            {grade}
                                        </span>
                                    ))}
                                </div>
                                <div style={{ ...styles.detailBox, marginRight: '0' }}>
                                    <strong>Absences:</strong>
                                    {discipline.absences.map((absence, index) => (
                                        <span key={index} style={{...styles.detailItem, ...(index === 0 ? styles.excused : styles.notExcused)}}>
                                            {index === 0 ? 'Excused' : 'Not Excused'} ({absence})
                                        </span>
                                    ))}
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No disciplines available</li>
                )}
            </ul>
        </div>
    );
};

export default StudentView;
