import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { Link } from 'react-router-dom';

const DisciplineManagement = () => {
    const { disciplines, error, fetchDisciplines } = useDisciplines();
    const { disciplineTypes, fetchDisciplineTypes } = useDisciplineTypes();
    const [editDiscipline, setEditDiscipline] = useState(null);
    const [newDiscipline, setNewDiscipline] = useState({ name: '', disciplineTypeId: '' });
    const [teacherQualifications, setTeacherQualifications] = useState([]);
    const [selectedQualification, setSelectedQualification] = useState('');

    useEffect(() => {
        fetchTeacherQualifications();
    }, []);

    const fetchTeacherQualifications = async () => {
        try {
            const response = await axios.get('/teacher-qualifications/');
            setTeacherQualifications(response.data);
        } catch (error) {
            console.error('Error fetching teacher qualifications:', error);
        }
    };

    const handleChange = (e, setter) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddDiscipline = async () => {
        try {
            const response = await axios.post('/disciplines/', newDiscipline);
            setNewDiscipline({ name: '', disciplineTypeId: '' });
            fetchDisciplines();
        } catch (error) {
            console.error('Error adding discipline:', error);
        }
    };

    const handleEditDiscipline = (discipline) => setEditDiscipline({ ...discipline });

    const handleSaveDiscipline = async () => {
        try {
            await axios.put(`/disciplines/${editDiscipline.id}`, editDiscipline);
            setEditDiscipline(null);
            fetchDisciplines();
        } catch (error) {
            console.error('Error saving discipline:', error);
        }
    };

    const handleDeleteDiscipline = async (disciplineId) => {
        if (window.confirm('Are you sure you want to delete this discipline?')) {
            try {
                await axios.delete(`/disciplines/${disciplineId}`);
                fetchDisciplines();
            } catch (error) {
                console.error('Error deleting discipline:', error);
            }
        }
    };

    const handleAddQualification = async () => {
        try {
            await axios.post(`/disciplines/${editDiscipline.id}/qualifications/${selectedQualification}`);
            fetchDisciplines();
        } catch (error) {
            console.error('Error adding qualification to discipline:', error);
        }
    };

    return (
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Discipline Management</h2>
                {error && <div className="alert alert-danger">Error: {error.message}</div>}
            </div>

            {/* Existing UI Code for Adding/Editing Disciplines */}

            <div className="mb-4">
                {/* Add New Qualification to Discipline */}
                {editDiscipline && (
                    <div className="card mt-4">
                        <h3 className="card-header">Add Qualification to Discipline</h3>
                        <div className="card-body">
                            <select
                                className="form-select mb-3"
                                value={selectedQualification}
                                onChange={(e) => setSelectedQualification(e.target.value)}
                            >
                                <option value="">Select a qualification</option>
                                {teacherQualifications.map((qualification) => (
                                    <option key={qualification.id} value={qualification.id}>
                                        {qualification.name}
                                    </option>
                                ))}
                            </select>
                            <button className="btn btn-primary" onClick={handleAddQualification}>
                                Add Qualification
                            </button>
                        </div>
                    </div>
                )}
            </div>

            {/* Existing UI Code for Displaying Disciplines */}
        </div>
    );
};

export default DisciplineManagement;
