import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { useParams } from 'react-router-dom';

const TeacherQualifications = () => {
    const { id } = useParams();
    const [qualifications, setQualifications] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQualifications = async () => {
            try {
                const response = await axios.get(`/teacher/${id}/qualifications`);
                setQualifications(response.data);
            } catch (error) {
                setError(error);
                console.error('There was an error fetching the qualifications!', error);
            }
        };

        fetchQualifications();
    }, [id]);

    return (
        <div className="container mt-4">
            <h2>Qualifications for Teacher ID: {id}</h2>
            {error && <div className="alert alert-danger">Error: {error.message}</div>}
            <ul className="list-group">
                {qualifications.length > 0 ? (
                    qualifications.map((qualification) => (
                        <li key={qualification.id} className="list-group-item">
                            <div className="qualification-info">
                                <span className="d-block mb-1">{qualification.degree}</span>
                                <span className="d-block mb-1">{qualification.institution}</span>
                                <span>{qualification.year}</span>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No qualifications available</li>
                )}
            </ul>
        </div>
    );
};

export default TeacherQualifications;
