import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { Link } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const DisciplineManagement = () => {
    const [disciplines, setDisciplines] = useState([]);
    const [disciplineTypes, setDisciplineTypes] = useState([]);
    const [newDiscipline, setNewDiscipline] = useState({
        name: '',
        disciplineTypeId: ''
    });
    const [editingDiscipline, setEditingDiscipline] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [disciplineToDelete, setDisciplineToDelete] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchDisciplines();
        fetchDisciplineTypes();
    }, []);

    const fetchDisciplines = async () => {
        try {
            const response = await axios.get('/disciplines/');
            setDisciplines(response.data);
        } catch (error) {
            setError(error);
        }
    };

    const fetchDisciplineTypes = async () => {
        try {
            const response = await axios.get('/discipline-types/');
            setDisciplineTypes(response.data);
        } catch (error) {
            console.error('Error fetching discipline types:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (editingDiscipline) {
            setEditingDiscipline(prevState => ({ ...prevState, [name]: value }));
        } else {
            setNewDiscipline(prevState => ({ ...prevState, [name]: value }));
        }
    };

    const handleCreateDiscipline = async () => {
        try {
            const response = await axios.post('/disciplines/', newDiscipline);
            setDisciplines([...disciplines, response.data]);
            setNewDiscipline({ name: '', disciplineTypeId: '' });
        } catch (error) {
            setError(error);
        }
    };

    const handleDeleteDiscipline = async () => {
        try {
            await axios.delete(`/disciplines/${disciplineToDelete.id}`);
            setDisciplines(disciplines.filter(discipline => discipline.id !== disciplineToDelete.id));
            setShowDeleteModal(false);
            setDisciplineToDelete(null);
        } catch (error) {
            setError(error);
        }
    };

    const handleUpdateDiscipline = async (id) => {
        try {
            const response = await axios.put(`/disciplines/${id}`, editingDiscipline);
            setDisciplines(disciplines.map(discipline => (discipline.id === id ? response.data : discipline)));
            setEditingDiscipline(null);
        } catch (error) {
            setError(error);
        }
    };

    const startEditing = (discipline) => {
        setEditingDiscipline(discipline);
    };

    const cancelEditing = () => {
        setEditingDiscipline(null);
    };

    const confirmDelete = (discipline) => {
        setDisciplineToDelete(discipline);
        setShowDeleteModal(true);
    };

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Discipline Management</h2>
            {error && <div className="alert alert-danger">Error: {error.message}</div>}
            <div className="mb-4">
                <h4>Add New Discipline</h4>
                <div className="mb-3">
                    <label htmlFor="disciplineName" className="form-label">Name:</label>
                    <input
                        type="text"
                        id="disciplineName"
                        name="name"
                        className="form-control"
                        value={newDiscipline.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="disciplineType" className="form-label">Discipline Type:</label>
                    <select
                        id="disciplineType"
                        name="disciplineTypeId"
                        className="form-select"
                        value={newDiscipline.disciplineTypeId}
                        onChange={handleInputChange}
                    >
                        <option value="">Select a type</option>
                        {disciplineTypes.map(type => (
                            <option key={type.id} value={type.id}>{type.disciplineType}</option>
                        ))}
                    </select>
                </div>
                <button className="btn btn-primary" onClick={handleCreateDiscipline}>Add Discipline</button>
            </div>
            <div>
                <h4>All Disciplines</h4>
                <ul className="list-group">
                    {disciplines.length > 0 ? (
                        disciplines.map(discipline => (
                            <li key={discipline.id} className="list-group-item d-flex justify-content-between align-items-center">
                                {editingDiscipline && editingDiscipline.id === discipline.id ? (
                                    <>
                                        <input
                                            type="text"
                                            name="name"
                                            value={editingDiscipline.name}
                                            onChange={handleInputChange}
                                            className="form-control mr-2"
                                        />
                                        <select
                                            name="disciplineTypeId"
                                            value={editingDiscipline.disciplineTypeId}
                                            onChange={handleInputChange}
                                            className="form-select mr-2"
                                        >
                                            <option value="">Select a type</option>
                                            {disciplineTypes.map(type => (
                                                <option key={type.id} value={type.id}>{type.disciplineType}</option>
                                            ))}
                                        </select>
                                        <button className="btn btn-success btn-sm mr-2" onClick={() => handleUpdateDiscipline(discipline.id)}>Save</button>
                                        <button className="btn btn-secondary btn-sm" onClick={cancelEditing}>Cancel</button>
                                    </>
                                ) : (
                                    <>
                                        <div>
                                            <strong>{discipline.name}</strong> - {disciplineTypes.find(type => type.id === discipline.disciplineTypeId)?.disciplineType}
                                        </div>
                                        <div>
                                            <button className="btn btn-primary btn-sm mr-2" onClick={() => startEditing(discipline)}>Edit</button>
                                            <button className="btn btn-danger btn-sm" onClick={() => confirmDelete(discipline)}>Delete</button>
                                        </div>
                                    </>
                                )}
                            </li>
                        ))
                    ) : (
                        <li className="list-group-item">No disciplines available</li>
                    )}
                </ul>
            </div>
            <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Confirm Deletion</Modal.Title>
                </Modal.Header>
                <Modal.Body>Are you sure you want to delete this discipline?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>Cancel</Button>
                    <Button variant="danger" onClick={handleDeleteDiscipline}>Delete</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default DisciplineManagement;
