import React from 'react';

const EditTeacherDiscipline = ({ item, subjects, teachers, onSave, onCancel }) => {
    const [updatedItem, setUpdatedItem] = React.useState({ ...item });
    const [qualificationError, setQualificationError] = React.useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem(prev => ({ ...prev, [name]: value }));
    };

    const handleSave = () => {
        // Check if the selected teacher has the qualification for the selected subject
        const selectedTeacher = teachers.find(teacher => teacher.id === updatedItem.teacherId);
        if (!selectedTeacher || !selectedTeacher.subjectQualificationIds) {
            setQualificationError('Selected teacher does not have any subject qualifications.');
            return;
        }

        const selectedSubject = subjects.find(subject => subject.id === updatedItem.subjectId);
        if (!selectedSubject || !selectedSubject.requiredQualificationIds) {
            setQualificationError('Selected subject does not have any required qualifications.');
            return;
        }

        const canTeach = selectedTeacher.subjectQualificationIds.some(qualificationId =>
            selectedSubject.requiredQualificationIds.includes(qualificationId)
        );

        if (!canTeach) {
            setQualificationError('Selected teacher does not have the qualification to teach this subject.');
            return;
        }

        // Clear any previous error messages
        setQualificationError(null);

        // Proceed with saving the association
        onSave(updatedItem);
    };

    return (
        <div>
            <div className="mb-2">
                <label htmlFor="subject">Subject</label>
                <select
                    className="form-control"
                    id="subject"
                    name="subjectId"
                    value={updatedItem.subjectId || ''}
                    onChange={handleChange}
                >
                    <option value="" disabled>Select a subject</option>
                    {subjects && subjects.map(subject => (
                        <option key={subject.id} value={subject.id}>
                            {subject.name}
                        </option>
                    ))}
                </select>
            </div>
            <div className="mb-2">
                <label htmlFor="teacher">Teacher</label>
                <select
                    className="form-control"
                    id="teacher"
                    name="teacherId"
                    value={updatedItem.teacherId || ''}
                    onChange={handleChange}
                >
                    <option value="" disabled>Select a teacher</option>
                    {teachers && teachers.map(teacher => (
                        <option key={teacher.id} value={teacher.id}>
                            {teacher.name}
                        </option>
                    ))}
                </select>
            </div>
            {qualificationError && (
                <div className="alert alert-danger mb-2">{qualificationError}</div>
            )}
            <div className="actions">
                <button className="btn btn-sm btn-success me-1" onClick={handleSave}>Save</button>
                <button className="btn btn-sm btn-secondary" onClick={onCancel}>Cancel</button>
            </div>
        </div>
    );
};

export default EditTeacherDiscipline;
