import React, { useState, useEffect } from 'react';

const EditTeacherDiscipline = ({ item, teachersMap, disciplinesMap, onSave, onCancel }) => {
    const [editItem, setEditItem] = useState({ ...item });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditItem((prev) => ({ ...prev, [name]: Number(value) }));
    };

    return (
        <div>
            <div className="form-group">
                <label>Discipline:</label>
                <select
                    className="form-control"
                    name="disciplineId"
                    value={editItem.disciplineId}
                    onChange={handleChange}
                >
                    {Object.entries(disciplinesMap).map(([id, name]) => (
                        <option key={id} value={id}>
                            {name}
                        </option>
                    ))}
                </select>
            </div>
            <div className="form-group">
                <label>Teacher:</label>
                <select
                    className="form-control"
                    name="teacherId"
                    value={editItem.teacherId}
                    onChange={handleChange}
                >
                    {Object.entries(teachersMap).map(([id, name]) => (
                        <option key={id} value={id}>
                            {name}
                        </option>
                    ))}
                </select>
            </div>
            <button className="btn btn-sm btn-success me-1" onClick={() => onSave(editItem)}>
                Save
            </button>
            <button className="btn btn-sm btn-secondary" onClick={onCancel}>
                Cancel
            </button>
        </div>
    );
};

export default EditTeacherDiscipline;
