import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const SchoolClassDetails = () => {
    const { schoolId, classId } = useParams();

    // State for managing students
    const [students, setStudents] = useState([]);
    const [error, setError] = useState(null);
    const [editStudent, setEditStudent] = useState(null);
    const [newStudent, setNewStudent] = useState({ name: '', numberInClass: '' });

    // Fetch students based on classId
    const fetchStudents = useCallback(async () => {
        try {
            const response = await axios.get(`/students/school-class/${classId}`);
            setStudents(response.data);
        } catch (error) {
            setError(error);
            console.error('Error fetching students:', error);
        }
    }, [classId]);

    // Fetch students when component mounts or classId changes
    useEffect(() => {
        fetchStudents();
    }, [fetchStudents]);

    // Handle edit student action
    const handleEditStudent = (student) => {
        setEditStudent({ ...student });
    };

    // Handle input change in the form
    const handleChange = (e) => {
        const { name, value } = e.target;
        if (editStudent) {
            setEditStudent((prev) => ({ ...prev, [name]: value }));
        } else {
            setNewStudent((prev) => ({ ...prev, [name]: value }));
        }
    };

    // Handle save student action
    const handleSaveStudent = async () => {
        try {
            if (editStudent) {
                await axios.put(`/students/${editStudent.id}`, {
                    ...editStudent,
                    schoolClassId: Number(classId),
                });
                setEditStudent(null);
            } else {
                await axios.post(`/students/`, {
                    ...newStudent,
                    schoolClassId: Number(classId),
                });
                setNewStudent({ name: '', numberInClass: '' });
            }
            fetchStudents();
        } catch (error) {
            console.error('Error saving student:', error);
        }
    };

    // Handle cancel edit action
    const handleCancelEdit = () => {
        setEditStudent(null);
    };

    // Handle delete student action
    const handleDeleteStudent = async (studentId) => {
        if (window.confirm('Are you sure you want to delete this student?')) {
            try {
                await axios.delete(`/students/${studentId}`);
                fetchStudents();
            } catch (error) {
                console.error('Error deleting student:', error);
            }
        }
    };

    return (
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Students for Class ID: {classId}</h2>
                {error && <div className="alert alert-danger">Error: {error.message}</div>}
            </div>

            <div className="mb-4">
                {/* Student Form */}
                <div className="card">
                    <h3 className="card-header">{editStudent ? 'Edit Student' : 'Add New Student'}</h3>
                    <div className="card-body">
                        <div className="form-group">
                            <input
                                type="text"
                                className="form-control mb-3"
                                placeholder="Student's Name"
                                name="name"
                                value={editStudent ? editStudent.name : newStudent.name}
                                onChange={handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="number"
                                className="form-control mb-3"
                                placeholder="Student's Number"
                                name="numberInClass"
                                value={editStudent ? editStudent.numberInClass : newStudent.numberInClass}
                                onChange={handleChange}
                            />
                        </div>
                        {editStudent ? (
                            <div className="d-flex justify-content-end">
                                <button className="btn btn-success me-1" onClick={handleSaveStudent}>
                                    Save
                                </button>
                                <button className="btn btn-secondary" onClick={handleCancelEdit}>
                                    Cancel
                                </button>
                            </div>
                        ) : (
                            <button className="btn btn-success" onClick={handleSaveStudent}>
                                Add Student
                            </button>
                        )}
                    </div>
                </div>
                {/* End Student Form */}
            </div>

            {/* Student List */}
            <ul className="list-group">
                {students.length > 0 ? (
                    students.map((student) => (
                        <li key={student.id} className="list-group-item mb-3">
                            <div className="d-flex align-items-center justify-content-between">
                                <div className="student-info">
                                    {editStudent && editStudent.id === student.id ? (
                                        <>
                                            <span className="d-block mb-1">{student.name}</span>
                                            <span>{student.numberInClass}</span>
                                        </>
                                    ) : (
                                        <>
                                            <span className="d-block mb-1">{student.name}</span>
                                            <span>{student.numberInClass}</span>
                                        </>
                                    )}
                                </div>
                                <div className="actions">
                                    {!editStudent && (
                                        <>
                                            <button
                                                className="btn btn-sm btn-success me-1"
                                                onClick={() => handleEditStudent(student)}
                                            >
                                                Edit
                                            </button>
                                            <Link
                                                to={`/admin/school/${schoolId}/class/${classId}/student/${student.id}/caregivers`}
                                                className="btn btn-primary btn-sm me-1"
                                            >
                                                Caregivers
                                            </Link>
                                            <button
                                                className="btn btn-sm btn-danger me-1"
                                                onClick={() => handleDeleteStudent(student.id)}
                                            >
                                                Delete
                                            </button>
                                        </>
                                    )}
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No students available</li>
                )}
            </ul>
            {/* End Student List */}
        </div>
    );
};

export default SchoolClassDetails;
