import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const useStudents = (classId) => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState(null);

    const fetchStudents = useCallback(async () => {
        try {
            const response = await axios.get(`students/school-class/${classId}`);
            setStudents(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the students!', error);
        }
    }, [classId]);

    useEffect(() => {
        fetchStudents();
    }, [fetchStudents]);

    return { students, error, fetchStudents };
};

const StudentForm = ({ student, onChange, onSave, onCancel, isEdit}) => (
    <div className="card">
        <h3 className="card-header">{isEdit ? 'Edit Student' : 'Add New Student'}</h3>
        <div className="card-body">
            <div className="form-group">
                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Student's Name"
                    name="name"
                    value={student.name}
                    onChange={onChange}
                />
            </div>
            <div className="form-group">
                <input
                    type="number"
                    className="form-control mb-3"
                    placeholder="Student's Number"
                    name="number" //TODO
                    value={student.numberInClass}
                    onChange={onChange}
                />
            </div>
            <button className="btn btn-success me-1" onClick={onSave}>
                {isEdit ? 'Save' : 'Add Student'}
            </button>
            {isEdit && (
                <button className="btn btn-secondary" onClick={onCancel}>
                    Cancel
                </button>
            )}
        </div>
    </div>
)

const SchoolClassDetails = () => {
    const { schoolId, classId } = useParams();
    const { students, error , fetchStudents} = useStudents(classId);
    const [editStudent, setEditStudent] = useState(null);
    const [newStudent, setNewStudent] = useState({ name: '', numberInClass: ''});

    const handeEditStudent = (student) => setEditStudent({...student});

    const handleChange = (e, setter) => {
        const {name, value} = e.target;
        setter((prev) => ({...prev, [name]:value}));
    }

    const handleSaveStudent = async() => {
        try{
            await axios.put(`/students/${editStudent.id}`,{
                ...editStudent,
                schoolClassId : Number(classId)
            });
            setEditStudent(null);
            fetchStudents();
        } catch(error){
            console.error('Error saving student:', error);
        }
    };

    const handleDeleteStudent = async(studentId) => {
        if(window.confirm('Are you sure you want to delete this student?')) {
            try{
                await axios.delete(`/students/${studentId}`);
                fetchStudents();
            } catch(error) {
                console.error('Error deleting student: ', error);
            }
        }
    };

    const handleAddStudent = async() => {
        try{
            await axios.put(`/students/`, {
                ...newStudent,
                schoolClassId: Number(classId)
            });
            setNewStudent({name: '', numberInClass: ''});
            fetchStudents()
        } catch(error) {
            console.error("Error adding student: ", error);
        }
    };

    return (
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Teachers for School ID: {schoolId}</h2>
                {error && <div className="alert alert-danger">Error: {error.message}</div>}
            </div>

            <div className="mb-4">
                <StudentForm
                    student={newStudent}
                    onChange={(e) => handleChange(e, setNewStudent())}
                    onSave={handleAddStudent()}
                />
            </div>

            <ul className="list-group">
                {students.length > 0 ? (
                    students.map((student) => (
                        <li key={student.id} className="list-group-item mb-3">
                            <div className="d-flex align-items-center justify-content-between">
                                <div className="student-info">
                                    {editStudent && editStudent.id === student.id ? (
                                        <StudentForm
                                            student={editStudent}
                                            onChange={(e) => handleChange(e, setEditStudent())}
                                            onSave={handleSaveStudent()}
                                            onCancel={() => setEditStudent(null)}
                                            isEdit={true}
                                        />
                                    ) : (
                                        <>
                                            <span className="d-block mb-1">{student.name}</span>
                                            <span>{student.numberInClass}</span>
                                        </>
                                    )}
                                </div>
                                <div className="actions">
                                    {!editStudent && (
                                        <>
                                            <button
                                                className="btn btn-sm btn-success me-1"
                                                onClick={() => handeEditStudent(student)}
                                            >
                                                Edit
                                            </button>
                                            <Link to={`/admin/school/${schoolId}/class/${classId}/student/${student.id}/caregivers`}
                                                  className="btn btn-primary btn-sm me-1">
                                                Caregivers
                                            </Link>
                                            <button
                                                className="btn btn-sm btn-danger me-1"
                                                onClick={() => handleDeleteStudent(student.id)}
                                            >
                                                Delete
                                            </button>
                                        </>
                                    )}
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <li className="list-group-item">No students available</li>
                )}
            </ul>
        </div>
        // <div className="container mt-4">
        //     <div className="mb-4">
        //         <h2>Students from Class with ID: {classId}</h2>
        //         {error && <div className="alert alert-danger">Error: {error.message}</div>}
        //     </div>
        //
        //     <ul className="list-group">
        //         {students.length > 0 ? (
        //             students.map((student) => (
        //                 <li key={student.id} className="list-group-item mb-3">
        //                     <div className="d-flex align-items-center justify-content-between">
        //                         <div className="students-info">
        //                             <span className="d-block mb-1">{student.name}</span>
        //                             <span>Number in Class: {student.numberInClass}</span>
        //                         </div>
        //                         <div className="actions">
        //                             <Link
        //                                 to={`/admin/school/${schoolId}/class/${classId}/student/${student.id}/caregivers`}
        //                                 className="btn btn-primary btn-sm me-1"
        //                             >
        //                                 Caregivers
        //                             </Link>
        //                         </div>
        //                     </div>
        //                 </li>
        //             ))
        //         ) : (
        //             <li className="list-group-item">No students available</li>
        //         )}
        //     </ul>
        // </div>
    );
};

export default SchoolClassDetails;
