import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const SchoolClassDetails = () => {
    const { schoolId, id } = useParams();
    const [students, setStudents] = useState([]);
    const [error, setError] = useState(null);

    const fetchStudents = useCallback(async () => {
        try {
            const response = await axios.get(`/students/school-class/${id}`);
            setStudents(response.data); // Assuming response.data is an array of students
        } catch (error) {
            setError(error);
            console.error('Error fetching students:', error);
        }
    }, [id]);

    useEffect(() => {
        fetchStudents();
    }, [fetchStudents]);

    if (error) {
        return <div className="container mt-4">Error fetching students: {error.message}</div>;
    }

    return (
        <div className="container mt-4">
            <div className="mb-4">
                <h2>Students from Class with ID: {id}</h2>
                {students.length > 0 ? (
                    <ul className="list-group">
                        {students.map((student) => (
                            <li key={student.id} className="list-group-item mb-3">
                                <div className="d-flex align-items-center justify-content-between">
                                    <div className="teacher-info">{/* Display student information */}</div>
                                    <div className="actions">
                                        <Link
                                            to={`/admin/school/${schoolId}/teacher/${student.id}/qualifications`}
                                            className="btn btn-primary btn-sm me-1"
                                        >
                                            Qualifications
                                        </Link>
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <div className="alert alert-info">No students available</div>
                )}
            </div>
        </div>
    );
};

export default SchoolClassDetails;
