import React, { useState, useEffect, useCallback } from 'react';
import axios from '../axiosInstance';
import { useParams, Link } from 'react-router-dom';

const useCurriculum = (classId) => {
    const [curriculum, setCurriculum] = useState(null);
    const [error, setError] = useState(null);

    const fetchCurriculum = useCallback(async () => {
        try {
            const response = await axios.get(`student-curriculum/school-class/${classId}`);
            setCurriculum(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the curriculum!', error);
        }
    }, [classId]);

    useEffect(() => {
        fetchCurriculum();
    }, [fetchCurriculum]);

    return { curriculum, error, fetchCurriculum, setCurriculum };
};

const useTeachersAndDisciplines = (curriculumId) => {
    const [teachersAndDisciplines, setTeachersAndDisciplines] = useState([]);
    const [error, setError] = useState(null);

    const fetchTeachersAndDisciplines = useCallback(async () => {
        try {
            const response = await axios.get(`curriculums-teachers-disciplines/student-curriculum/${curriculumId}`);
            setTeachersAndDisciplines(response.data);
        } catch (error) {
            setError(error);
            console.error('There was an error fetching the teachers and disciplines!', error);
        }
    }, [curriculumId]);

    useEffect(() => {
        if (curriculumId) {
            fetchTeachersAndDisciplines();
        }
    }, [curriculumId, fetchTeachersAndDisciplines]);

    return { teachersAndDisciplines, error, fetchTeachersAndDisciplines };
};

const fetchAllTeachersAndDisciplines = async () => {
    try {
        const [teachersResponse, disciplinesResponse] = await Promise.all([
            axios.get('/teachers'),
            axios.get('/disciplines'),
        ]);

        const teachers = teachersResponse.data.reduce((acc, teacher) => {
            acc[teacher.id] = teacher.name;
            return acc;
        }, {});

        const disciplines = disciplinesResponse.data.reduce((acc, discipline) => {
            acc[discipline.id] = discipline.name;
            return acc;
        }, {});

        return { teachers, disciplines };
    } catch (error) {
        console.error('There was an error fetching teachers and disciplines!', error);
        throw error;
    }
};

const CurriculumDetails = () => {
    const { schoolId, classId } = useParams();
    const { curriculum, error, fetchCurriculum, setCurriculum } = useCurriculum(classId);
    const { teachersAndDisciplines, error: teachersError, fetchTeachersAndDisciplines } = useTeachersAndDisciplines(curriculum?.id);
    const [editCurriculum, setEditCurriculum] = useState(null);
    const [newCurriculum, setNewCurriculum] = useState({ semester: '', year: '' });
    const [teachersMap, setTeachersMap] = useState({});
    const [disciplinesMap, setDisciplinesMap] = useState({});
    const [editTeacherDiscipline, setEditTeacherDiscipline] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { teachers, disciplines } = await fetchAllTeachersAndDisciplines();
                setTeachersMap(teachers);
                setDisciplinesMap(disciplines);
            } catch (error) {
                console.error('Error fetching teachers and disciplines:', error);
            }
        };

        fetchData();
    }, []);

    const handleEditCurriculum = (curriculum) => setEditCurriculum({ ...curriculum });

    const handleChange = (e, setter) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleSaveCurriculum = async () => {
        try {
            await axios.put(`/student-curriculum/${editCurriculum.id}`, {
                ...editCurriculum,
                schoolClassId: Number(classId)
            });
            setEditCurriculum(null);
            fetchCurriculum();
        } catch (error) {
            console.error('Error saving curriculum:', error);
        }
    };

    const handleDeleteCurriculum = async (id) => {
        if (window.confirm('Are you sure you want to delete this curriculum?')) {
            try {
                await axios.delete(`/student-curriculum/${id}`);
                setCurriculum(null); // Set the curriculum to null to immediately reflect the deletion
                fetchCurriculum();
            } catch (error) {
                console.error('Error deleting curriculum: ', error);
            }
        }
    };

    const handleAddCurriculum = async () => {
        try {
            if (curriculum !== null) {
                console.log("A curriculum already exists for this class.");
                return;
            }

            await axios.post(`/student-curriculum/`, {
                ...newCurriculum,
                schoolClassId: Number(classId)
            });

            setNewCurriculum({ semester: '', year: '' });
            fetchCurriculum();
        } catch (error) {
            console.error("Error adding curriculum: ", error);
        }
    };

    const handleEditTeacherDiscipline = (item) => setEditTeacherDiscipline({ ...item });

    const handleSaveTeacherDiscipline = async () => {
        try {
            await axios.put(`/curriculums-teachers-disciplines/${editTeacherDiscipline.id}`, editTeacherDiscipline);
            setEditTeacherDiscipline(null);
            fetchTeachersAndDisciplines();
        } catch (error) {
            console.error('Error saving teacher and discipline:', error);
        }
    };

    return (
        <div className="container mt-4">
            <div className="mb-4 d-flex justify-content-between align-items-center">
                <h2 className="mb-0">School Class Details for School ID: {schoolId}</h2>
            </div>
            {error && error.response && error.response.status !== 500 &&
                <div className="alert alert-danger text-center">
                    Error: {error.message}
                </div>
            }

            {!curriculum && (
                <div className="mb-4">
                    <div className="card">
                        <h3 className="card-header">Add New Curriculum</h3>
                        <div className="card-body">
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control mb-3"
                                    placeholder="Semester"
                                    name="semester"
                                    value={newCurriculum.semester}
                                    onChange={(e) => handleChange(e, setNewCurriculum)}
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="number"
                                    className="form-control mb-3"
                                    placeholder="Grade"
                                    name="year"
                                    value={newCurriculum.year}
                                    onChange={(e) => handleChange(e, setNewCurriculum)}
                                />
                            </div>
                            <button
                                className="btn btn-success me-1"
                                onClick={handleAddCurriculum}
                            >
                                Add Curriculum
                            </button>
                        </div>
                    </div>
                </div>
            )}

            <ul className="list-group">
                {curriculum != null ? (
                    <>
                        <li key={curriculum.id} className="list-group-item mb-3">
                            <div className="d-flex align-items-center justify-content-between">
                                <div className="curriculum-info">
                                    {editCurriculum && editCurriculum.id === curriculum.id ? (
                                        <>
                                            <div className="mb-2">
                                                <span>Semester: </span>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    name="semester"
                                                    value={editCurriculum.semester}
                                                    onChange={(e) => handleChange(e, setEditCurriculum)}
                                                />
                                            </div>
                                            <div className="mb-2">
                                                <span>Grade: </span>
                                                <input
                                                    type="number"
                                                    className="form-control"
                                                    name="year"
                                                    value={editCurriculum.year}
                                                    onChange={(e) => handleChange(e, setEditCurriculum)}
                                                />
                                            </div>
                                        </>
                                    ) : (
                                        <>
                                            <span className="d-block mb-1">Semester: {curriculum.semester}</span>
                                            <span>Grade: {curriculum.year}</span>
                                        </>
                                    )}
                                </div>
                                <div className="actions">
                                    {editCurriculum && editCurriculum.id === curriculum.id ? (
                                        <button className="btn btn-sm btn-success me-1" onClick={handleSaveCurriculum}>
                                            Save
                                        </button>
                                    ) : (
                                        <button className="btn btn-sm btn-success me-1" onClick={() => handleEditCurriculum(curriculum)}>
                                            Edit
                                        </button>
                                    )}
                                    <button className="btn btn-sm btn-danger me-1" onClick={() => handleDeleteCurriculum(curriculum.id)}>
                                        Delete
                                    </button>
                                </div>
                            </div>
                        </li>
                        <li className="list-group-item">
                            <h4>Teachers and Disciplines</h4>
                            {teachersError && <div className="alert alert-danger text-center">Error: {teachersError.message}</div>}
                            <ul className="list-group">
                                {Array.isArray(teachersAndDisciplines) && teachersAndDisciplines.length > 0 ? (
                                    teachersAndDisciplines.map((item) => (
                                        <li key={item.id} className="list-group-item">
                                            {editTeacherDiscipline && editTeacherDiscipline.id === item.id ? (
                                                <>
                                                    <div className="form-group">
                                                        <label>Discipline:</label>
                                                        <select
                                                            className="form-control"
                                                            value={editTeacherDiscipline.disciplineId}
                                                            onChange={(e) =>
                                                                setEditTeacherDiscipline((prev) => ({
                                                                    ...prev,
                                                                    disciplineId: Number(e.target.value),
                                                                }))
                                                            }
                                                        >
                                                            {Object.entries(disciplinesMap).map(([id, name]) => (
                                                                <option key={id} value={id}>
                                                                    {name}
                                                                </option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                    <div className="form-group">
                                                        <label>Teacher:</label>
                                                        <select
                                                            className="form-control"
                                                            value={editTeacherDiscipline.teacherId}
                                                            onChange={(e) =>
                                                                setEditTeacherDiscipline((prev) => ({
                                                                    ...prev,
                                                                    teacherId: Number(e.target.value),
                                                                }))
                                                            }
                                                        >
                                                            {Object.entries(teachersMap).map(([id, name]) => (
                                                                <option key={id} value={id}>
                                                                    {name}
                                                                </option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                    <button className="btn btn-sm btn-success me-1" onClick={handleSaveTeacherDiscipline}>
                                                        Save
                                                    </button>
                                                </>
                                            ) : (
                                                <>
                                                    <span>Discipline: {disciplinesMap[item.disciplineId]}</span>
                                                    <span> | Teacher: {teachersMap[item.teacherId]}</span>
                                                    <button className="btn btn-sm btn-success me-1" onClick={() => handleEditTeacherDiscipline(item)}>
                                                        Edit
                                                    </button>
                                                </>
                                            )}
                                        </li>
                                    ))
                                ) : (
                                    <li className="list-group-item">No teachers and disciplines available</li>
                                )}
                            </ul>
                        </li>
                    </>
                ) : (
                    <li className="list-group-item">No curriculum available</li>
                )}
            </ul>
        </div>
    );
};

export default CurriculumDetails;
