// SchoolList.jsx
import React, { useState, useEffect } from 'react';
import axios from '../axiosInstance';
import { Link } from 'react-router-dom';
import './SchoolList.css'; // Import CSS file for styling

const SchoolList = () => {
    const [schools, setSchools] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSchools = async () => {
            try {
                const response = await axios.get('/school/');
                setSchools(response.data);
            } catch (error) {
                setError(error);
                console.error('There was an error fetching the schools!', error);
            }
        };

        fetchSchools();
    }, []);

    if (error) {
        return <div className="error-message">Error: {error.message}</div>;
    }

    return (
        <div className="school-list-container">
            <h2>Schools</h2>
            <ul className="school-list">
                {schools.map(school => (
                    <li key={school.id} className="school-item">
                        {school.name}
                        <Link to={`/school/${school.id}/teachers`} className="teachers-link">
                            <button className="view-teachers-button">Teachers</button>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SchoolList;
